import React, { Component } from 'react'
import axios from 'axios'

class Signup extends Component {
    constructor() {
        super()
        this.initialState = {
            bio: '',
            email: '',
            firstname: '',
            displayname: '',
            user_password: '',
        }
        this.state = this.initialState
    }

    // submitForm is the function used to handle the onSubmit event for the form in the render 
    submitForm = (event) => {
        event.preventDefault(); 

        let payload = {bio,
            email,
            firstname, 
            displayname, 
            user_password,
        } = this.state
        payload.active = true
        payload.profilepic = ''
        console.log(payload, 'asdfasdf', this.state)

        let response = axios.post('https://localhost:3001/users/signup', payload)
        console.log(response)
    }

    // handleInput is the function responsible for updating the state everytime user presses a key
    handleInput = ({target: {name, value}}) => this.setState({ [name]: value })

    render() {
        let {
            state: {
                bio,
                email,
                firstname, 
                displayname, 
                user_password,
            },
            submitForm,
            handleInput,
        } = this

        return(
            <form onSubmit={submitForm}>
                <h1>Sign-Up for Meme Me</h1>
                <label htmlFor="email">email: </label>
                <input 
                    id="email"
                    type="email"
                    name="email"
                    value={email}
                    onChange={handleInput}
                    placeholder="email@domain.org"
                /><br/>
                <label htmlFor="displayname">displayname: </label>
                <input 
                    id="displayname"
                    type="text"
                    name="displayname"
                    value={displayname}
                    // pattern=
                    onChange={handleInput}
                    placeholder="displayname"
                /><br/>
                <label htmlFor="user_password">password: </label>
                <input 
                    id="user_password"
                    type="user_password"
                    name="user_password"
                    value={user_password}
                    onChange={handleInput}
                    placeholder="password"
                /><br/>
                <label htmlFor="firstname">full name: </label>
                <input 
                    id="firstname"
                    type="text"
                    name="firstname"
                    value={firstname}
                    onChange={handleInput}
                    placeholder="Name"
                /><br/>
                <label htmlFor="bio">bio: </label>
                <input 
                    id="bio"
                    type="text"
                    name="bio"
                    value={bio}
                    onChange={handleInput}
                    placeholder="Tell us about yourself"
                /><br/>
                <button type="submit">Sign Up</button>
            </form>
        )
    }
}

export default Signup
