import React, { Component } from 'react'

class Signup extends Component {
    constructor() {
        super()
        this.initialState = {
            email: '',
            displayName: '',
            fullName: '',
            bio: '',
        }
        this.state = this.initialState
    }

    // submitForm is the function used to handle the onSubmit event for the form in the render 
    submitForm = (event) => {
        event.preventDefault();

        console.log(event.target)
    }

    // handleInput is the function responsible for updating the state everytime user presses a key
    handleInput = ({target: {id, value}}) => this.setState({ [id]: value })

    render() {
        let {
            state: {
                email,
                displayName, 
                fullName, 
                bio,
            },
            handleInput,
            submitForm,
        } = this

        return(
            <>
                <form onSubmit={submitForm}>
                    <h1>Sign-Up for Meme Me</h1>
                    <input 
                        id="email"
                        type="email"
                        value={email}
                        pattern={emailObject.pattern}
                        onChange={handleInput}
                        placeholder="email@domain.org"
                    /><br/>
                    <input 
                        id="displayName"
                        type="text"
                        value={displayName}
                        // pattern=
                        onChange={handleInput}
                        placeholder="Display Name"
                    /><br/>
                    <input 
                        id="fullName"
                        type="text"
                        value={fullName}
                        // pattern=
                        onChange={handleInput}
                        placeholder="Name"
                    /><br/>
                    <input 
                        id="bio"
                        type="text"
                        value={bio}
                        // pattern=
                        onChange={handleInput}
                        placeholder="Tell us about yourself"
                    /><br/>
                    <button type="submit">Sign Up</button>
                </form>
            </>
        )
    }
}

export default Signup
